    "%matplotlib inline\n",
    "from sklearn.metrics import confusion_matrix\n",
    "import itertools\n",
    "import matplotlib.pyplot as plt\n",
    "\n",
    "y_pred = model.predict_classes(test_x)\n",
    "\n",
    "cm = confusion_matrix(y_true = test_df.Severity_1.values, y_pred = y_pred )\n",
